import {useEffect, useState} from "react"
import "./portfolio.scss"
import PortfolioList from "../portfolioList/PortfolioList"
import {
    featuredPortfolio,
    webPortfolio,
    mobilePortfolio,
    contentPortfolio
}from "../../data";

export default function Portfolio() {
    const [selected, setSelected]= useState("featured");
    const[data, setData]= useState([]);
    useEffect(()=> {
        switch(selected){
            case "featured":
                setData(featuredPortfolio);
                break;
        case "featured":
                    setData(featuredPortfolio);
                    break;
        }
    }, [selected])
    const list = [
        {
          id: "featured",
          title: "Featured",
        },
        {
          id: "web",
          title: "Web App",
        },
        {
          id: "mobile",
          title: "Mobile App",
        },
        {
          id: "design",
          title: "Design",
        },
        {
          id: "content",
          title: "Content",
        },
      ];

    return (
        <div className="portfolio" id="portfolio"> 
        <h1> Portfolio </h1>
        <ul>
            {list.map((item)=>(
                <PortfolioList 
                title ={item.title} 
                active={selected=== item.id}
                setSelected = {setSelected}
                id={item.id}
                />
            ))}
        </ul>

        <div className="container">
            <div className="item"> 
                <img src="assets/middle.png" alt=""/>
                <h3>Banking App</h3>
            </div>

            <div className="item"> 
                <img src="assets/middle.png" alt=""/>
                <h3>Banking App</h3>
            </div>


            <div className="item"> 
                <img src="assets/middle.png" alt=""/>
